import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      message: 'tada.app',
      newTodo: '',
      todos: [{
        title: 'Learn React',
        done: false
      },
    {
      title: 'Learn JSX',
      done: false
    }]
    };
  }

  newTodoChanged(event) {
    this.setState({
        newTodo: event.target.value
      });
  }

  formSubmitted(event) {
    event.preventDefault();
    this.setState({
      newTodo: '',
        todos: [...this.state.todos, {
          title: this.state.newTodo,
          done: false
        }]
      });
  }

  toggleTodoDone(event, index) {
    console.log(event.target.checked);
    const todos = [...this.state.todos]; // copy the array
    todos[index] = {...todos[index]}; // copy the todo
    todos[index].done = event.target.checked; // update done property on copied todo
    console.log(todos);
    this.setState({
      todos
      });
  }

  render() {
    return (
      <div className="App">
        <h1>{this.state.message}</h1>

        <form onSubmit={(event) => this.formSubmitted(event)}>

          <label htmlFor="newTodo">New Todo</label>

        <input
            onChange={(event) => this.newTodoChanged(event)}
            id="newTodo"
            name="newTodo"
            value={this.state.newTodo}
            />

          <button type="submit">Add Todo</button>

        </form>

        <ul>
          {this.state.todos.map((todo, index) => {
            return <li key={todo.title}>
              <input onChange={(event) => this.toggleTodoDone(event, index)} type="checkbox" />
              <span style={{ textDecoration: todo.done ? 'line-through' : 'inherit'}}>{todo.title}</span>
            </li>
          })}
        </ul>

      </div>
    );
  }
}

export default App;
