import React, { Component } from 'react';
import './App.css';
import NewTodoForm from './NewTodoForm'
import TodoList from './TodoList'
import MenuBar from './MenuBar'
import Dashboard from './Dashboard'

import Web3 from 'web3'

class App extends Component {

  constructor() {
    super();
    this.web3 = new Web3(new Web3.providers.HttpProvider('http://localhost:8545'));
    this.state = {
      newTodo: '',
      todos: [],
      time: new Date()
    };
  }

  componentWillMount() {
    if(this.web3) {
      this.setState({isConnected: true});
    }
  }

  newTodoChanged(event) {
    this.setState({
        newTodo: event.target.value,
      });
  }

  ethAmountChanged(event) {
    this.setState({
        ethAmount: event.target.value,
      });
  }

  formSubmitted(event) {
    event.preventDefault();
    this.setState({
      timeLeft: '',
      newTodo: '',
      ethAmount: '',
        todos: [...this.state.todos, {
          title: this.state.newTodo,
          amount: this.state.ethAmount,
          timeLeft: this.state.timeLeft,
          done: false
        }]
      });
  }

  toggleTodoDone(event, index) {
    const todos = [...this.state.todos]; // copy the array
    todos[index] = {
      ...todos[index],
      done: event.target.checked
    };
    this.setState({
      todos
      });
  }

  removeTodo(index) {
    const todos = [...this.state.todos]; // copy the array
    todos.splice(index, 1);
    this.setState({
      todos
      });
  }


  render() {
    return (
      <div className="App">

        <MenuBar />

        <NewTodoForm
          formSubmitted={this.formSubmitted.bind(this)}
          newTodoChanged={this.newTodoChanged.bind(this)}
          newTodo={this.state.newTodo}
          ethAmountChanged={this.ethAmountChanged.bind(this)}
          ethAmount={this.state.ethAmount}
        />

      <TodoList
        todos={this.state.todos}
        toggleTodoDone={this.toggleTodoDone.bind(this)}
        removeTodo={this.removeTodo.bind(this)}
        />

      <Dashboard
        todos={this.state.todos}
        />

        <div>
          <h2>Is connected?:</h2><br/> {this.state.isConnected?'Connected to local node':'Not Connected'}
          <br/>
          <br/>
          <h2>The number of peers:</h2><br/> {this.state.peers}
          <br/>
          <br/>
          <h2>Node info:</h2><br/> {this.state.version}
        </div>

      </div>
    );
  }
}

export default App;
